		private static CompiledText Text;

		private static int YScroll;

		public static void Init()
		{
			if (Text == null)
			{
				Text = new CompiledText(CreditsText, UI.UsableWidth, UI.BoldSmallTextStyle);
			}
			YScroll = Main.ResolutionHeight;
		}

		public static void Update()
		{
			if (UI.MainUI.IsBackButtonTriggered())
			{
				Text = null;
				UI.MainUI.PrevMenu();
			}
			else if (-YScroll < Text.Height - (Main.ResolutionHeight / ScrollingSpeed) && !UI.MainUI.IsUpButtonDown())
			{
				YScroll -= ScrollingSpeed;
				if (UI.MainUI.IsDownButtonDown())
				{
					YScroll -= ScrollingSpeed;
				}
			}
		}

		public static void Draw()
		{
			Text.Draw(Main.SpriteBatch, new Rectangle(Main.ResolutionWidth / 20, YScroll, UI.UsableWidth, Text.Height), new Color(255, 255, 255, 255), new Color(255, 212, 0, 255));
		}
	}
}
